export const addItemToCart = (cartItems, itemToAdd) => {
  console.log(itemToAdd);
  const itemExistsInCart = cartItems.find((item) => item.id === itemToAdd.id);
  console.log(itemExistsInCart);
  return [...cartItems, { ...itemToAdd, quantity: 1 }];
};
