const cartState = {
  items: [
    {
      id: 1,
      title: "Test",
      price: 6,
      description: "Test Description",
    },
  ],
};

const cartReducer = (state = cartState, action) => {
  switch (action.type) {
    default:
      return state;
  }
};

export default cartReducer;
