import { addItemToCart } from "./cart.utils";

const cartState = {
  items: [
    {
      id: 1,
      title: "Test",
      price: 6,
      description: "Test Description",
      quantity: 1,
    },
    {
      id: 2,
      title: "Test 2",
      price: 10,
      description: "Test Description 2",
      quantity: 1,
    },
    {
      id: 3,
      title: "Test 2",
      price: 10,
      description: "Test Description 2",
      quantity: 1,
    },
  ],
};

const cartReducer = (state = cartState, action) => {
  switch (action.type) {
    case "ADD_ITEM": {
      return {
        items: addItemToCart(state.itmes, action.payload),
      };
    }
    default:
      return state;
  }
};

export default cartReducer;
